/**
 * Splits a provided array into arrays of size chunkSize.
 * Useful when you need to bulk submit array contents of a certain size.
 * @param chunkSize number of items in each chunk
 * @param array source array to generate chunk array
 * @returns array of arrays, each with a length of chunkSize
 */
export function chunkArray(chunkSize: number, array: any[]): any[][] {
	const chunkedArrays: any[][] = array.reduce((resultArray, item, index) => {
		const chunkIndex = Math.floor(index / chunkSize);
		if (!resultArray[chunkIndex]) {
			resultArray[chunkIndex] = [];
		}
		resultArray[chunkIndex].push(item);
		return resultArray;
	}, []);
	return chunkedArrays;
}