/**
 * @module getSha1Hash
 * @category Helper
 */
 import { createHash } from "crypto";

 /**
  * Applies a hashing function for calculating unique records. 
  * @param input string in which to hash
  * @returns sha1 hash of input string representation 
  */
 export function getSha1Hash(input: string): string {
   const hash = createHash("sha1");
   hash.update(input);
   return hash.digest("hex");
 }
 
 /**
  * Applies a hashing function for calculating unique records.
  * @param input string in which to hash
  * @returns sha256 hash of input string representation
  */
 export function getSha256Hash(data: string) {
   const hash = createHash("sha256");
   hash.update(data);
   return hash.digest("hex");
 }
 