import { LoggingLevel } from "./logger";

export interface Log {
  info(message?: any, ...optionalParams: any[]): undefined|void;
  warn(message?: any, ...optionalParams: any[]): undefined|void;
  error(message?: any, ...optionalParams: any[]): undefined|void;
  trace(message?: any, ...optionalParams: any[]): undefined|void;
  instrument(instrumentLevel: LoggingLevel, message?: any, ...optionalParams: any[]): undefined|void;
}
