/**
 * Function that returns a new number array, with the start and end parameters as upper & lower bounds respectively. 
 * Used to iterate 
 * @param start lower bound of array, this value will be the first value of index 0 of array
 * @param end upper bound of array, this value will be the last value of last index of array
 * @returns An array of numbers
 */
export function range(start: number, end: number): Array<number> {
	return [...Array(1 + end - start).keys()].map((value) => start + value);
}
