import { chunkArray } from "../src";

describe("chunkArray", () => {
  test("returns chunks of 1 length when below n", () => {
    const testArray: number[] = Array.from(Array(50).keys());
    const returnVal = chunkArray(100, testArray);
    expect(returnVal.length).toBe(1);
  });

  test("returns chunks of more than 1 length when below n", () => {
    const testArray: number[] = Array.from(Array(1000).keys());
    const returnVal = chunkArray(100, testArray);
    expect(returnVal.length).toBe(10);
  });
});
