import { getSha1Hash, getSha256Hash } from "../src";

describe("hashUtils", () => {
    test("returns sha1", () => {
        expect(getSha1Hash("{}")).toBe("bf21a9e8fbc5a3846fb05b4fa0859e0917b2202f");
    });

    test("returns sha256", () => {
        expect(getSha256Hash("{}")).toBe("44136fa355b3678a1146ad16f7e8649e94fb4fc21fe77e8310c060f61caaff8a");
    });
});