import { includesAny } from "../src";

describe("includesAny", () => {
    test("returns false when given an empty array", () => {
        const returnVal = includesAny('apples', []);
        expect(returnVal).toBe(false);
      });

      test("returns false on empty array with empty string", () => {
        const returnVal = includesAny('', []);
        expect(returnVal).toBe(false);
      });

      test("returns true when included and array is size of 1", () => {
        const returnVal = includesAny('apples', ['app']);
        expect(returnVal).toBe(true);
      });

      test("returns true when included and array is size of N", () => {
        const returnVal = includesAny('apples', ['bee', 'cee', 'dee', 'app']);
        expect(returnVal).toBe(true);
      });

      test("returns false when not included and array is size of N", () => {
        const returnVal = includesAny('apples', ['bee', 'cee', 'dee', 'crayon']);
        expect(returnVal).toBe(false);
      });

      test("returns false when not included and array is size of 1", () => {
        const returnVal = includesAny('apples', ['crayon']);
        expect(returnVal).toBe(false);
      });
      
      test("returns true when array contains different case and match", () => {
        const returnVal = includesAny('apples', ['APPLES']);
        expect(returnVal).toBe(true);
      });

      test("returns true when inputString and array contain different case and match", () => {
        const returnVal = includesAny('ApPles', ['APPLES']);
        expect(returnVal).toBe(true);
      });

});
