import { range } from "../src";

describe("range", () => {
    test("returns array of right size", () => {
        const returnVal = range(1, 2);
        expect(returnVal.length).toBe(2);
      });

      test("returns array of right size, when numbers are same", () => {
        const returnVal = range(1, 1);
        expect(returnVal.length).toBe(1);
      });

      test("returns array if difference between start and end are large", () => {
        const returnVal = range(0, 1000);
        expect(returnVal.length).toBe(1001);
      });

      test("start range can be negative", () => {
        const returnVal = range(-1, 1);
        expect(returnVal.length).toBe(3);
      });

      test("both start and end can be negative", () => {
        const returnVal = range(-10, -8);
        expect(returnVal.length).toBe(3);
      });

      test("returns empty array if range is invalid, with start value larger than end value", () => {
        const returnVal = range(100, 99);
        expect(returnVal.length).toBe(0);
      });

});
